import React, {useState} from 'react'
import './App.css';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import About from './components/About';
import Alert from './components/Alert';
// import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

function App() {
  const [mode, setMode] = useState("light");
  const [modeName, setModeName] = useState("DarkMode");
  const [alert, setAlert] = useState(null);

  const showAlert = (message, type) => {
    setAlert({
      message: message,
      type: type
    });
    setTimeout(() => {
      setAlert(null);
    }, 2000);
  }

  const removeAlert = () => {
    setAlert(null);
  }


  const toggleMode = () => {   // this function is for toggle "darkmode" and "lightmode"
    if(mode === "light"){
      setMode("dark");
      setModeName("LightMode");
      document.body.style.backgroundColor = "#212529";

      showAlert("Success: Dark mode is enabled", "success");
    }
    else{
      setMode("light");
      setModeName("DarkMode");
      document.body.style.backgroundColor = "white";

      showAlert("Success: Light mode is enabled", "success");
    }
  }

  var disabled;
  if(alert == null){
    disabled = "";
  }
  else{
    disabled = "disabled";
  }

  const toggleTitle = () => {
    setInterval(() => {
      document.title = "React App";

      setTimeout(() => {
        document.title = "Devyansh App";
      }, 1000);

    }, 2000);
  }


  return (
    <>
      <Router>
      <Routes>

        {/* <Navbar mode={mode} modeName={modeName} toggleMode={toggleMode} toggleTitle={toggleTitle} disabled={disabled}></Navbar> */}
        {/* <Alert alert={alert} removeAlert={removeAlert}></Alert> */}

        {/* <Switch>
          <Route path='/about'>
            <About mode={mode}></About>
          </Route>
          <Route path='/'>
            <TextForm mode={mode} heading="Enter the text to analyze below" showAlert={showAlert}></TextForm>
          </Route>
        </Switch> */}


        <Route path="/" element={<TextForm mode={mode} heading="Enter the text to analyze below" showAlert={showAlert}></TextForm>} />
        <Route path="/about" element={<About mode={mode}></About>} />
      </Routes>

      </Router>
    </>
  );
}

export default App;
